-- ----------------------------
-- Table structure for cms_article
-- ----------------------------
DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) DEFAULT NULL COMMENT '标题',
  `content` longtext COMMENT '文章内容',
  `picture` varchar(200) DEFAULT NULL COMMENT '标题图片',
  `channel_id` int(11) DEFAULT NULL COMMENT '所属栏目',
  `category_id` int(11) DEFAULT NULL COMMENT '所属分类',
  `user_id` int(11) DEFAULT NULL COMMENT '用户Id',
  `hits` int(11) DEFAULT NULL COMMENT '点击次数',
  `hot` int(11) DEFAULT NULL COMMENT '是否热门',
  `status` int(11) DEFAULT NULL COMMENT '0：刚发布；2：草稿；1审核通过；-1 审核未通过;3:禁用',
  `deleted` int(11) DEFAULT NULL COMMENT '0:正常,1:删除',
  `created` datetime DEFAULT NULL COMMENT '发布时间',
  `updated` datetime DEFAULT NULL COMMENT '更新时间',
  `commentCnt` int(11) NOT NULL DEFAULT '0' COMMENT '评论数量',
  `tousuCnt` int(11) NOT NULL DEFAULT '0' COMMENT '投诉次数',
  PRIMARY KEY (`id`),
  KEY `FK_CMS_ARTI_REFERENCE_CMS_USER` (`user_id`) USING BTREE,
  KEY `FK_CMS_ARTI_REFERENCE_CMS_CHAN` (`channel_id`) USING BTREE,
  KEY `FK_CMS_ARTI_REFERENCE_CMS_CATE` (`category_id`) USING BTREE,
  CONSTRAINT `cms_article_ibfk_1` FOREIGN KEY (`category_id`) REFERENCES `cms_category` (`id`),
  CONSTRAINT `cms_article_ibfk_2` FOREIGN KEY (`channel_id`) REFERENCES `cms_channel` (`id`),
  CONSTRAINT `cms_article_ibfk_3` FOREIGN KEY (`user_id`) REFERENCES `cms_user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=164 DEFAULT CHARSET=utf8 COMMENT='文章表';

