-- Table structure for cms_article_tag
-- ----------------------------
DROP TABLE IF EXISTS `cms_article_tag`;
CREATE TABLE `cms_article_tag` (
  `aid` int(11) NOT NULL COMMENT '文章Id',
  `tid` int(11) DEFAULT NULL COMMENT '标签Id',
  PRIMARY KEY (`aid`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='文章标签关系表';

-- ----------------------------
-- Records of cms_article_tag
-- ----------------------------

-- ----------------------------
-- Table structure for cms_article_vote
-- ----------------------------
DROP TABLE IF EXISTS `cms_article_vote`;
CREATE TABLE `cms_article_vote` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_id` int(11) DEFAULT NULL COMMENT '文章Id',
  `user_id` int(11) DEFAULT NULL COMMENT '用户Id',
  `option` varchar(255) CHARACTER SET utf8 DEFAULT NULL COMMENT '选项',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=latin1 COMMENT='投票表';

