DROP TABLE IF EXISTS `cms_user`;
CREATE TABLE `cms_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(20) DEFAULT NULL COMMENT '登录名称',
  `headimg` varchar(255) DEFAULT NULL COMMENT '用户头像',
  `password` varchar(50) DEFAULT NULL COMMENT '登录密码',
  `nickname` varchar(20) DEFAULT NULL COMMENT '昵称',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `gender` int(11) DEFAULT NULL COMMENT '性别，1：男；2：女',
  `locked` int(11) DEFAULT '0' COMMENT '0:正常,1:禁用',
  `score` int(11) DEFAULT NULL COMMENT '积分',
  `role` varchar(1) DEFAULT '0' COMMENT '0:普通用户,1:管理员',
  `url` varchar(200) DEFAULT NULL COMMENT '个人博客网址',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=238 DEFAULT CHARSET=utf8;

